import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';

function Detalle() {
    const { curso } = useParams();
    const { nombre } = useParams();
    const [alumnos, setAlumnos] = useState([]);
    const [showModal, setShowModal] = useState(false);

    const toggleModal = () => {
        setShowModal(!showModal);
    };
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:8080/curso/' + curso + '/alumno');
                setAlumnos(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        }
        fetchData();
    }, []);
    return (
        <div className="container">
            <h2>Curso: {nombre}</h2>
            <h4>Lista de estudiantes</h4>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th scope="col">ID Alumno</th>
                        <th scope="col">Nombre</th>
                        <th scope="col">Telefono</th>
                        <th scope="col">Fecha Nacimiento</th>
                        <th scope="col">Email</th>
                        <th scope="col">Operaciones</th>
                    </tr>
                </thead>
                <tbody>
                    {alumnos.map((al, id) => (
                        <tr key={id} >
                            <th scope="row">{al.id}</th>
                            <td>{al.nombreAlumno}</td>
                            <td>{al.numeroTelefono}</td>
                            <td>{al.fechaNacimiento}</td>
                            <td>{al.email}</td>
                            <td> eliminar</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={toggleModal}>Abrir Modal</button>
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={toggleModal}>&times;</span>
                        <h2>Este es un modal</h2>
                        <p>Aqu√≠ puedes agregar cualquier contenido que desees mostrar en el modal.</p>
                    </div>
                </div>
            )}
        </div>
    )

}
export default Detalle
