import './App.css';
import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Curso from './curso';

function App() {
  const [cursos, setCursos] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/curso');
        setCursos(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }
    fetchData();
  }, []);

  return (
    <div className="App container">
      <Router>
        <switch><Route path="/curso" exact component={Curso} /></switch>

      </Router>
      <h2>Cursos de facultad Estudios Tecnologicos</h2>
      <h6>LISTADO DE CURSOS DISPONIBLES</h6>
      <div className='row'>
        {cursos.map((elemento, id) => (
          <div className="col-sm-6">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{elemento.nombreCurso}</h5>
                <p className="card-text">Impartido por: <i className='textDocente'>{elemento.nombreProfesor}</i></p>
                <p className="card-text">Estudiantes inscritos: <i className='textDocente'>{elemento.nombreProfesor}</i></p>
                <a href="#" className="btn btn-primary">Ver</a>
                <Link to="/">Ir a la p√°gina dos</Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
export default App;
