import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';
import Modal from 'react-modal';

function Detalle() {
    const { curso } = useParams();
    const { nombre } = useParams();
    const [alumnos, setAlumnos] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [alumno,setAlumno]=useState('')

    const toggleModal = () => {
        setShowModal(!showModal);
    };
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:8080/curso/' + curso + '/alumno');
                setAlumnos(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        }
        fetchData();
    }, []);
    return (
        <div className="container">
            <h2>Curso: {nombre}</h2>
            <h4>Lista de estudiantes</h4>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th scope="col">ID Alumno</th>
                        <th scope="col">Nombre</th>
                        <th scope="col">Telefono</th>
                        <th scope="col">Fecha Nacimiento</th>
                        <th scope="col">Email</th>
                        <th scope="col">Operaciones</th>
                    </tr>
                </thead>
                <tbody>
                    {alumnos.map((al, id) => (
                        <tr key={id} >
                            <th scope="row">{al.id}</th>
                            <td>{al.nombreAlumno}</td>
                            <td>{al.numeroTelefono}</td>
                            <td>{al.fechaNacimiento}</td>
                            <td>{al.email}</td>
                            <td><button className="btn btn-info" onClick={toggleModal}>Abrir Modal</button> eliminar</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <Modal isOpen={showModal}
                onRequestClose={toggleModal}
                contentLabel="Detalle Alumno"
            >
                <h2>Ejemplo de Modal</h2>
                <p>Aqu√≠ puedes agregar cualquier contenido que desees mostrar en el modal.</p>
                <button onClick={toggleModal}>Cerrar Modal</button>
            </Modal>
        </div>
    )

}
export default Detalle
